package TP2;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =30;
		double b=1.5;
		System.out.println(a++);
		System.out.println(a);
		System.out.println(--a);

		System.out.println(++b);
		System.out.println(b--);
		System.out.println(b);
	}

}
